import { useEffect } from 'react';

import './App.css';
import SearchIcon from './search.svg';

const API_URL = 'https://api.themoviedb.org/3/search/movie?api_key=01f9af4886c7fb97667aea5735ad3fd4'

const movie1 = {
    adult: false,
  backdrop_path: '/2PDTWfuBWQKVC7aPAqJK5UCpz08.jpg',
  genre_ids: [2, 3], // Array of genre IDs
  id: 557,
  original_language: 'en',
}

const App = () => {

    const searchMovies = async (title) => {
        const response = await fetch(`${API_URL}&query=${title}`);
        const data = await response.json();

        console.log(data.results);
    }

    useEffect(() => {
        searchMovies('Spiderman');
    }, []);
    return(
       <div className="app">
        <h1>MovieLand</h1>
        <div className="search">
            <input
            placeholder="Search for movies"
            value = "Iron Man"
            onChange={() => {}}
            />
            <img
            src={SearchIcon}
            alt="search"
            onClick={() => {}}
            />
        </div>
       </div>


    );
}

export default App;
